Page.content:
/* syntax highlight*/
pre {
  width: 100%;
  position: relative;
  border: none;
  padding: 0;
  margin: 0;
}
pre code {
  display: block;
  box-sizing: border-box;
  overflow-x: auto;
  font-family: Source Code Pro, Monaco, Menlo, Consolas, monospace;
  margin: 1.2em 0;
  padding: 15px 15px 10px 15px;
  line-height: 1.2em;
  width: 100%;
  border-radius: 5px;
  box-shadow: 1px 1px 2px rgba(0,0,0,0.125);
  font-size: 13px;
  color: #333;
  background: #f7f8f8;
}
figure.highlight,
.codeblock {
  background: #f7f8f8;
  margin: 10px 0;
  line-height: 1.1em;
  padding-top: 15px;
  margin: 1em 0;
  border-radius: 5px;
  box-shadow: 1px 1px 2px rgba(0,0,0,0.125);
}
figure.highlight figcaption,
.codeblock figcaption {
  font-size: 13px;
  padding: 0 15px 20px;
  margin: 0;
  background: #f7f8f8;
  color: #999;
}
figure.highlight figcaption a,
.codeblock figcaption a {
  float: right;
  color: #40759b;
  font-weight: bold;
}
figure.highlight table,
.codeblock table {
  width: 100%;
  margin: 0;
  padding-bottom: 10px;
  border-collapse: collapse;
  position: relative;
  display: block;
  overflow-x: auto;
}
figure.highlight pre,
.codeblock pre,
figure.highlight .gutter,
.codeblock .gutter,
figure.highlight .code,
.codeblock .code,
figure.highlight .tag,
.codeblock .tag {
  background-color: inherit;
  font-family: Source Code Pro, Monaco, Menlo, Consolas, monospace;
  border: none;
  padding: 0;
  margin: 0;
  cursor: text;
}
figure.highlight .gutter,
.codeblock .gutter,
figure.highlight .code,
.codeblock .code {
  vertical-align: top;
}
figure.highlight.plain .gutter,
.codeblock.plain .gutter {
  display: none;
}
figure.highlight .gutter,
.codeblock .gutter {
  background: #f7f8f8;
  border-right: 1px solid #e6e6e6;
  padding: 0.3em 15px;
}
figure.highlight .gutter .line,
.codeblock .gutter .line {
  color: #aaa;
}
figure.highlight .line,
.codeblock .line {
  font-size: 13px;
  color: #333;
}
figure.highlight .line.marked,
.codeblock .line.marked {
  background: #f5e3de;
}
figure.highlight .code,
.codeblock .code {
  padding: 0.3em 15px 0.3em 1em;
  width: 100%;
}
.gist .line,
.gist .line-number {
  font-family: Source Code Pro, Monaco, Menlo, Consolas, monospace;
  font-size: 1em;
  margin: 0 0 5px 0;
}
/* code highlight color*/
.highlight .comment,
.highlight .title {
  color: #888;
}
.highlight .meta,
.highlight .tag {
  color: #324977;
}
.highlight .attr {
  color: #118a04;
}
.highlight .string,
.highlight .value,
.highlight .inheritance,
.highlight .header,
.highlight .ruby .symbol,
.highlight .xml .cdatabody {
  color: #920000;
}
.highlight .keyword,
.highlight .javascript .function {
  color: #ec25ec;
}
.highlight .number,
.highlight .preprocessor,
.highlight .built_in,
.highlight .literal,
.highlight .params,
.highlight .constant {
  color: #fb6a00;
}
.highlight.apacheconf .code .common,
.highlight.apacheconf .code .nomarkup,
.highlight.apacheconf .code .attribute,
.highlight.apacheconf .code .variable,
.highlight.apacheconf .code .cbracket,
.highlight.apacheconf .code .keyword {
  color: #0086b3;
}
.highlight.apacheconf .code .sqbracket {
