Page.content:
/* reset */
body,
dl,
dt,
dd,
ul,
ol,
li,
h1,
h2,
h3,
h4,
h5,
h6,
p,
img,
blockquote,
button,
pre,
fieldset,
form,
input,
select,
option {
  margin: 0;
  padding: 0;
  border: 0;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  font-weight: bold;
  overflow: hidden;
}
s,
i,
u,
em {
  font-style: normal;
}
img,
button {
  border: none;
}
form,
input,
select,
option {
  outline: none;
}
input:-webkit-autofill {
  -webkit-box-shadow: 0 0 0px 1000px #fff inset;
}
div {
  box-sizing: border-box;
}
a,
button.submit {
  color: #40759b;
  text-decoration: none;
  -webkit-transition: all 0.1s ease-in;
  -moz-transition: all 0.1s ease-in;
  -o-transition: all 0.1s ease-in;
  transition: all 0.1s ease-in;
}
a:focus {
  outline: none;
}
a:hover,
a:active {
  color: #40759b;
}
.clear {
  clear: both;
}
.hide {
  display: none;
  opacity: 0;
  height: 0px;
}
blockquote,
.stressed {
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  margin: 1.5em 0 1.5em 1em;
  padding: 10px;
  color: #555;
  border: 1px solid #e6e6e6;
  border-left: 5px solid #40759b;
}
strong,
b,
em {
  font-weight: bold;
}
.hidden1 {
  display: none;
}
/* iconfont */
