/* layout */
body {
  font-family: "Source Code Pro", Tahoma, "Helvetica Neue", Helvetica, "Hiragino Sans GB", "Microsoft YaHei Light", "Microsoft YaHei", "Source Han Sans CN", "WenQuanYi Micro Hei", Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  font-size: 14px;
  color: #333;
  width: 100%;
  height: 100%;
  background: #d8e2eb url("../img/bg.jpg") no-repeat top center;
}
.layout-g {
  letter-spacing: -0.31em;
  *letter-spacing: normal;
  word-spacing: -0.43em;
  *word-spacing: -0.43em;
  text-rendering: optimizespeed;
  display: -webkit-flex;
  -webkit-flex-flow: row wrap;
  display: -ms-flexbox;
  -ms-flex-flow: row wrap;
  -ms-align-content: flex-start;
  -webkit-align-content: flex-start;
}
.layout-l,
.layout-r {
  display: inline-block;
  *display: inline;
  zoom: 1;
  letter-spacing: normal;
  word-spacing: normal;
  vertical-align: top;
  text-rendering: auto;
}
.layout-l {
  width: 75%;
  *width: 74.969%;
}
.layout-r {
  width: 25%;
  *width: 24.969%;
}
.body_container {
  padding: 0 50px;
  max-width: 1200px;
  margin: 0 auto;
}
.content_container {
  padding-right: 40px;
}
/* header */
#header {
  text-align: left;
  position: relative;
  min-height: 300px;
}
#header .site-name {
  padding-top: 100px;
}
#header .site-name h1 {
  padding: 0;
  margin: 0;
  height: 0;
  overflow: hidden;
}
#header .site-name .description {
  padding: 0.6em 0 0;
  color: #fff;
  text-shadow: 1px 2px 3px #113f6e;
  white-space: normal;
  word-wrap: break-word;
  line-height: 26px;
}
#logo {
  font: bold 42px/1.2 "宋体";
  white-space: nowrap;
  color: #fff;
  text-shadow: 1px 3px 6px #113f6e;
}
#nav-menu {
  margin: 20px 0;
  padding: 0;
  position: absolute;
  right: 0;
  top: 0;
}
#nav-menu a {
  display: inline-block;
  padding: 3px 18px 3px;
  line-height: 30px;
  color: #fff;
  font-size: 16px;
  border: 1px solid transparent;
}
#nav-menu a:hover,
#nav-menu a.current {
  border-bottom: 1px solid #fff;
  -webkit-box-shadow: 0 0 10px #adc2d7 inset;
  box-shadow: 0 0 10px #adc2d7 inset;
}
/* sidebar */
#sidebar {
  word-wrap: break-word;
  -webkit-transition: all 0.6s ease-in-out;
  transition: all 0.6s ease-in-out;
  padding: 55px 0;
}
#sidebar .widget {
  margin-bottom: 20px;
  padding: 10px 20px 20px 20px;
  border-radius: 4px;
  -webkit-box-shadow: 1px 2px 3px #adc2d7;
  box-shadow: 1px 2px 3px #adc2d7;
  border: 1px solid #adc2d7;
  background: #fff;
}
#sidebar .widget a {
  color: #333;
}
#sidebar .widget a:hover {
  color: #40759b;
  text-decoration: underline;
}
#sidebar .widget .widget-title,
#sidebar .widget .comments-title {
  color: #40759b;
  line-height: 2.7;
  margin-top: 0;
  font-size: 16px;
  border-bottom: 1px solid #d8e2eb;
  display: block;
  font-weight: normal;
}
#sidebar .widget .tagcloud {
  margin-top: 10px;
}
#sidebar .widget .tagcloud a {
  line-height: 1.5;
  padding: 5px;
}
#sidebar .widget ul,
#sidebar .widget ol {
  list-style: none;
  padding: 0;
}
#sidebar .widget ul li,
#sidebar .widget ol li {
  list-style: none;
  margin: 5px 0;
  line-height: 1.5;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}
#sidebar .widget .category-list-count {
  padding-left: 5px;
  color: #40759b;
}
#sidebar .widget .category-list-count:before {
  content: "(";
}
#sidebar .widget .category-list-count:after {
  content: ")";
}
#sidebar .search-pla {
  display: block;
  height: 52px;
}
#search-form {
  position: absolute;
  bottom: -52px;
  right: 0;
  padding: 20px 0;
  color: #333;
  width: 25%;
}
#search-form label {
  position: relative;
  display: block;
  margin: 0;
  width: 100%;
  height: 100%;
}
#search-form label:before {
  font-family: "iconfont" !important;
  content: '\e007';
  position: absolute;
  left: 15px;
  top: 9px;
}
#search-form input {
  box-sizing: border-box;
  height: 32px;
  padding: 7px 11px 7px 35px;
  line-height: 16px;
  width: 100%;
  border-radius: 15px;
  background: #fff;
  -webkit-box-shadow: 1px 2px 3px #adc2d7;
  box-shadow: 1px 2px 3px #adc2d7;
  border: 1px solid #adc2d7;
}
#result-wrap {
  position: absolute;
  top: 56px;
  width: 120%;
  left: -50px;
  box-sizing: content-box;
  padding: 20px;
  background: #fff;
  color: #333;
  border-radius: 4px;
  -webkit-box-shadow: 1px 2px 3px #adc2d7;
  box-shadow: 1px 2px 3px #adc2d7;
  border: 1px solid #adc2d7;
}
#result-wrap:before {
  content: '';
  position: absolute;
  display: inline-block;
  border-width: 0 12px 12px 12px;
  border-style: solid;
  border-color: #adc2d7 transparent;
  left: 56px;
  top: -12px;
}
#result-wrap:after {
  content: '';
  position: absolute;
  display: inline-block;
  border-width: 0 12px 12px 12px;
  border-style: solid;
  border-color: #fff transparent;
  left: 56px;
  top: -10px;
}
#result-mask {
  position: fixed;
  left: 0;
  top: 0;
}
#search-result {
  width: 100%;
  box-sizing: content-box;
  padding-right: 20px;
  margin-right: -20px;
  max-height: 400px;
  overflow-y: auto;
}
#search-result .tips {
  text-align: center;
}
#search-result .tips .fa {
  font-size: 60px;
  color: #b3d2ef;
}
#search-result .item {
  border-top: 1px solid #d8e2eb;
  padding: 8px 0;
}
#search-result .item:first-child {
  border-top: none;
}
#search-result .item a:hover {
  text-decoration: none;
}
#search-result .item .title {
  white-space: nowrap;
  line-height: 1.8;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  font-size: 14px;
  color: #40759b;
}
#search-result .item .title:hover {
  text-decoration: underline;
}
#search-result .item .time {
  text-align: right;
  font-size: 12px;
}
#search-result .item .tags {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}
#search-result .item .tags span {
  display: inline-block;
  margin-right: 5px;
  font-size: 12px;
}
#search-result .item .tags span:before {
  font-family: "iconfont" !important;
  content: '\e008';
}
/* post */
.post {
  padding: 25px;
  background: #fff;
  border-radius: 4px;
  -webkit-box-shadow: 1px 2px 3px #adc2d7;
  box-shadow: 1px 2px 3px #adc2d7;
  border: 1px solid #adc2d7;
  margin-bottom: 40px;
}
.post .post-title {
  margin: 0;
  color: #40759b;
  text-align: left;
  border-bottom: 1px solid #d8e2eb;
  padding-bottom: 10px;
  font-weight: bold;
  font-size: 22px;
  line-height: 1.1;
}
.post .post-title a {
  color: #40759b;
}
.post .post-title a:hover {
  text-decoration: underline;
}
.post .post-meta {
  padding: 0;
  margin: 10px 0 0;
  color: #333;
  font-size: 14px;
  line-height: 24px;
  min-height: 24px;
  text-indent: 0.15em;
}
.post .post-meta:after {
  content: '';
  display: table;
  clear: both;
}
.post .post-meta span {
  display: inline-block;
  height: 16px;
  line-height: 16px;
  margin-right: 2px;
}
.post .post-meta span:after {
  content: ' | ';
}
.post .post-meta span:last-child:after {
  content: ' ';
}
.post .post-meta a {
  color: #40759b;
}
.post .post-meta a:hover {
  color: #40759b;
  border-bottom: 1px solid #40759b;
}
.post .post-meta .category:before {
  font-family: "iconfont" !important;
  content: "\e002";
  padding-right: 0.3em;
}
.post .post-meta .date:before {
  font-family: "iconfont" !important;
  content: "\e005";
  padding-right: 0.3em;
}
.post .post-meta #busuanzi_value_page_pv {
  height: 16px;
  line-height: 16px;
}
.post .post-meta #busuanzi_value_page_pv:before {
  font-family: "iconfont" !important;
  content: "\e00d";
  padding-right: 0.3em;
}
.post .comment-count {
  float: right;
  display: inline-block;
  text-indent: 0.15em;
}
.post .comment-count:before {
  vertical-align: bottom;
  font-family: "iconfont" !important;
  content: "\e006";
  padding-right: 0.3em;
}
.post .comment-count:hover {
  color: #40759b;
  border-bottom: 1px solid #40759b;
}
.post .comment-count span:after {
  content: '';
  display: none;
}
.post .post-content {
  clear: left;
  font-size: 15px;
  line-height: 1.77;
  color: #333;
  padding-top: 15px;
  text-align: justify;
  text-justify: distribute;
  word-break: normal;
}
.post .post-content h2 {
  font-size: 18px;
}
.post .post-content h3 {
  font-size: 16px;
}
.post .post-content h4 {
  font-size: 14px;
}
.post .post-content h5 {
  font-size: 13px;
}
.post .post-content h6 {
  font-size: 12px;
}
.post .post-content p {
  margin: 0 0 1.234em;
}
.post .post-content p code {
  margin: 0 5px;
  padding: 0 5px;
  font-family: Source Code Pro, Monaco, Menlo, Consolas, monospace;
  color: #4d4d4c;
  background: #eee;
