  margin-top: 0.1em;
  position: relative;
  text-align: center;
}
.post .post-content blockquote p,
.post .post-content .stressed p {
  margin: 0;
}
.post .post-content img {
  max-width: 100%;
  padding: 0.5em 0;
  margin: auto;
  display: block;
}
.post .post-content hr {
  margin: 2.4em auto;
  border: none;
  border-top: 1px solid #d8e2eb;
  position: relative;
}
.post .post-content ul,
.post .post-content ol {
  margin: 0;
  padding: 0 0 0 2em;
}
.post .post-content ul code,
.post .post-content ol code {
  margin: 0 5px;
  padding: 0 5px;
  font-family: Source Code Pro, Monaco, Menlo, Consolas, monospace;
  color: #4d4d4c;
  background: #eee;
  font-size: 0.95em;
}
.post .post-content ul a,
.post .post-content ol a {
  color: #40759b;
  padding-bottom: 2px;
  word-break: break-all;
}
.post .post-content ul a:hover,
.post .post-content ol a:hover {
  text-decoration: underline;
}
.post .tags {
  padding-bottom: 0.5em;
  text-align: right;
}
.post .tags a {
  margin-right: 0.5em;
}
.post .tags a:before {
  font-family: "iconfont" !important;
  content: "\e008";
  padding-right: 0.3em;
}
.post .post-share::after {
  content: ' ';
  display: block;
  clear: both;
}
/* for archive page starts*/
.post-archive {
  font-size: 15px;
  line-height: 2;
  padding: 2em 0;
}
.post-archive ul,
.post-archive ol,
.post-archive li {
  list-style: none;
}
.post-archive h2 {
  margin: 0 0 15px -51px;
  font-weight: bold;
  font-size: 24px;
  letter-spacing: -1px;
  background: #d8e2eb;
  height: 100px;
  width: 100px;
  border-radius: 50%;
  text-align: center;
  line-height: 100px;
  color: #5483b1;
}
.post-archive h2:before {
  font-family: "iconfont" !important;
  font-weight: normal;
  content: "\e005";
  margin-right: 0.1em;
}
.archive-wrap {
  position: relative;
  border-left: 2px solid #d8e2eb;
  margin-left: 140px;
  padding: 3em 0;
}
.archive-wrap.tag-cat {
  margin-left: 60px;
}
.archive-wrap:before,
.archive-wrap:after {
  content: '';
  position: absolute;
  left: -6px;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #d8e2eb;
}
.archive-wrap:before {
  top: 0;
}
.archive-wrap:after {
  bottom: 0;
}
.archive-wrap .listing li {
  margin-bottom: 50px;
  position: relative;
}
.archive-wrap .listing li a {
  color: #333;
}
.archive-wrap .listing li a:hover {
  color: #5483b1;
}
.archive-wrap .listing li a:before {
  content: '';
  position: absolute;
  z-index: 1;
  left: -11px;
  bottom: -10px;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: #fff;
  border: 1px solid #d8e2eb;
  -webkit-box-shadow: 0 0 4px #d8e2eb inset;
  box-shadow: 0 0 4px #d8e2eb inset;
}
.archive-wrap .listing li a:after {
  content: '';
  position: absolute;
  z-index: 2;
  left: -5px;
  bottom: -4px;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #d8e2eb;
  -webkit-box-shadow: 0 0 3px #d8e2eb inset;
  box-shadow: 0 0 3px #d8e2eb inset;
}
.archive-wrap .listing li:hover a:before {
  border: 1px solid #5483b1;
  -webkit-box-shadow: 0 0 4px #5483b1 inset;
  box-shadow: 0 0 4px #5483b1 inset;
}
.archive-wrap .listing li:hover a:after {
  background: #5483b1;
}
.archive-wrap .listing li:hover .tite {
  border-bottom: 1px solid #5483b1;
}
.archive-wrap .listing li:hover .tite:after {
  background: #5483b1;
}
.archive-wrap .listing li:hover .date {
  border: 1px solid #5483b1;
}
.archive-wrap .tite {
  position: relative;
  bottom: -4px;
  display: inline-block;
  border-bottom: 1px solid #d8e2eb;
  line-height: 1.2em;
  padding: 0 30px;
  max-width: 100%;
  box-sizing: border-box;
}
.archive-wrap .tite:after {
  content: '';
  position: absolute;
  right: 0;
  bottom: -4px;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: #d8e2eb;
}
.archive-wrap .date {
  position: absolute;
  left: -112px;
  width: 100px;
  bottom: 0;
  height: 30px;
  line-height: 30px;
  text-align: center;
  border-radius: 15px 30px 0 15px;
  border: 1px solid #d8e2eb;
  -webkit-box-shadow: 0 0 4px #d8e2eb inset;
  box-shadow: 0 0 4px #d8e2eb inset;
}
/* for archive page ends */
/* footer */
#footer {
  width: 100%;
  margin-top: 50px;
  padding: 30px 0;
}
#footer .footer-info {
  line-height: 2;
  color: #333;
  text-align: center;
  font-size: 0.925em;
}
#footer .footer-info a {
  color: #40759b;
  margin: 0 6px;
}
#footer .footer-info span {
  margin: 0 6px;
}
#footer .footer-info .fa {
  display: inline-block;
  margin: 0 6px 0 0;
  padding: 0 2px;
}
#footer .footer-info .fa.fa-github {
  color: #000;
}
#footer .footer-info .fa.fa-github:hover {
  color: #4078c0;
}
#footer .footer-info .fa.fa-weibo {
  color: #e6162d;
}
#footer .footer-info .fa.fa-weibo:hover {
  color: #f93;
}
#footer .footer-info .fa.fa-email {
  color: #756e78;
}
#footer .footer-info .fa.fa-email:hover {
  color: #000;
}
#footer .footer-info p a {
  margin: 0;
}
/* page navigator */
.page-navigator {
  list-style: none;
  padding: 0 0 25px 0;
  font-size: 14px;
  text-align: center;
}
.page-navigator .space {
  border: none;
  padding: 5px 5px;
}
.page-navigator .current {
  background: #fff;
}
.page-navigator .current:hover {
  border-bottom-color: #adc2d7;
}
.page-navigator .page-number,
.page-navigator .extend {
  display: inline-block;
  margin: 0 5px 5px 0;
}
.page-navigator a,
.page-navigator span {
  display: inline-block;
  height: 25px;
  line-height: 25px;
  padding: 5px 9px;
  text-align: center;
  -webkit-box-shadow: 1px 2px 3px #adc2d7;
  box-shadow: 1px 2px 3px #adc2d7;
  border: 1px solid #adc2d7;
}
.page-navigator a:hover,
.page-navigator span:hover {
  border-bottom-color: #fff;
}
.page-navigator a.prev:before,
.page-navigator span.prev:before {
  font-family: "iconfont" !important;
  content: "\e00f";
  padding-right: 0.3em;
}
.page-navigator a.next:after,
.page-navigator span.next:after {
  font-family: "iconfont" !important;
  content: "\e00e";
  padding-left: 0.3em;
}
/* label title*/
.label-title {
  margin-top: 1.1em;
  font-size: 22px;
  color: #333;
  font-weight: bold;
}
.label-title .fa {
  display: inline-block;
  background: #f2f7fb;
  color: #5483b1;
  padding: 0 10px;
  border-radius: 30px 0 0 30px;
  margin: 0 5px;
  font-size: 20px;
}
/* read more*/
.readmore a {
  font-size: 14px;
  color: #40759b;
  margin: -20px 0 0;
  padding: 5px 10px;
  float: right;
}
.readmore a:after {
  content: ">>";
  padding-left: 0.15em;
}
.readmore a:hover {
  -webkit-box-shadow: 0 -1px 4px #5483b1 inset;
  box-shadow: 0 -1px 4px #5483b1 inset;
}
/* post navigator*/
.post-nav {
  overflow: hidden;
  margin-top: 15px;
  margin-bottom: 20px;
  padding: 10px;
  white-space: nowrap;
  border-top: 1px solid #d8e2eb;
}
.post-nav a {
  display: inline-block;
  max-width: 49%;
  line-height: 25px;
  font-size: 15px;
  color: #555;
  border-bottom: none;
  position: relative;
  white-space: normal;
  box-sizing: border-box;
  text-decoration: underline;
}
.post-nav a.pre {
  float: left;
  padding-left: 1.2em;
}
.post-nav a.pre:before {
  position: absolute;
  left: 0;
  top: 0;
  font-family: "iconfont" !important;
  content: "\e00f";
  padding-right: 0.3em;
}
.post-nav a:hover {
  color: #40759b;
}
.post-nav a.next {
  float: right;
  text-align: right;
  padding-right: 1.2em;
}
.post-nav a.next:after {
  position: absolute;
  right: 0;
  top: 0;
  font-family: "iconfont" !important;
  content: "\e00e";
  padding-left: 0.3em;
}
/* back-to-top */
#totop {
  position: fixed;
  right: 50px;
  bottom: 50px;
  display: block;
  visibility: hidden;
  width: 32px;
  height: 54px;
  line-height: 56px;
  overflow: hidden;
  color: #40759b;
  text-align: center;
  text-shadow: 0px 2px 2px #40759b;
  opacity: 0;
  -webkit-transition: visibility 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335), opacity 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335), -webkit-transform 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335);
  -moz-transition: visibility 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335), opacity 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335), -moz-transform 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335);
  transition: visibility 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335), opacity 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335), transform 0.6s cubic-bezier(0.6, 0.04, 0.98, 0.335);
}
#totop:before {
  font-family: "iconfont" !important;
  content: '\e00c';
  font-size: 54px;
  margin-left: -0.2em;
}
#totop.launch {
  opacity: 0;
  -webkit-transform: translateY(-500px);
  -moz-transform: translateY(-500px);
  -ms-transform: translateY(-500px);
  transform: translateY(-500px);
  pointer-events: none;
}
#totop.show {
  visibility: visible;
  opacity: 1;
}
/* about page */
.author-page {
  padding: 40px 0 20px 0;
  text-align: center;
}
.author-page:after {
  content: "";
  clear: both;
  display: block;
}
.author-page .photo {
  display: inline-block;
  vertical-align: top;
  box-sizing: content-box;
  padding: 0 30px 30px 30px;
  width: 180px;
